template.content:
import request from '@/router/axios';
import validate from "@/util/validate";

export function fetch${ClassName}Tree(query) {
  return request({
    url: '/${urlPrefix}/tree',
    method: 'get',
    params: query
  })
}
export function page${ClassName}(query) {
  return request({
    url: '/${urlPrefix}/',
    method: 'get',
    params: query
  })
}

export function save${ClassName}(obj) {
  return request({
    url: '/${urlPrefix}/',
    method: 'post',
    data: obj
  })
}

export function find${ClassName}(id) {
  return request({
    url: '/${urlPrefix}/' + id,
    method: 'get'
  })
}

export function remove${ClassName}(id) {
  return request({
    url: '/${urlPrefix}/' + id,
    method: 'delete'
  })
}

export function validateUnique${ClassName}(rule, value, callback, id) {
  validate.isUnique(rule, value, callback, '/${urlPrefix}/checkByProperty?id='+validate.toStr(id))
}
